package hello.batch.actual.batch.chunk.processor;import org.modelmapper.ModelMapper;import org.springframework.batch.item.ItemProcessor;import hello.batch.actual.batch.domain.Product;import hello.batch.actual.batch.domain.ProductVO;public class FileItemProcessor implements ItemProcessor<ProductVO, Product> {	@Override	public Product process(ProductVO productVO) {		ModelMapper mapper = new ModelMapper();		Product product = mapper.map(productVO, Product.class);		return product;	}}