package hello.batch.actual.batch.chunk.writer;import java.util.List;import org.springframework.batch.item.ItemWriter;import hello.batch.actual.batch.domain.ApiRequestVO;import hello.batch.actual.batch.domain.ApiResponseVO;import hello.batch.actual.service.AbstractApiService;public class ApiItemWriter3 implements ItemWriter<ApiRequestVO> {	private final AbstractApiService apiService;	public ApiItemWriter3(AbstractApiService apiService) {		this.apiService = apiService;	}	@Override	public void write(List<? extends ApiRequestVO> items) throws Exception {		ApiResponseVO responseVO = apiService.service(items);		System.out.println("responseVO = " + responseVO);	}}