package hello.batch.actual.batch.job.api;import java.sql.ResultSet;import java.sql.SQLException;import java.util.List;import javax.sql.DataSource;import org.springframework.jdbc.core.JdbcTemplate;import hello.batch.actual.batch.domain.ProductVO;import hello.batch.actual.batch.rowmapper.ProductRowMapper;public class QueryGenerator {	public static ProductVO[] getProductArray(DataSource dataSource) {		JdbcTemplate jdbcTemplate = new JdbcTemplate(dataSource);		List<ProductVO> productVOList = jdbcTemplate.query("select type from product group by type ", new ProductRowMapper() {			@Override			public ProductVO mapRow(ResultSet rs, int rowNum) throws SQLException {				return ProductVO.builder().type(rs.getString("type")).build();			}		});		return productVOList.toArray(new ProductVO[] {});	}}